INSERT INTO RECETTE(NOM_RECETTE, NOTE, CONTENU) VALUES ('Pâtes à la Bolognaise', 1, 'Faites boullir de l''eau salée. Rajouter un petit peu de beurre dans l''eau bouillante. Rajouter ensuite les pâtes, et laisser bouillir pendant 8 minutes. Ensuite, égoûter les pates, et y verser un dé de beurre, et mélanger. Ensuite, rajouter de la sauce bolognaise, et du fromage rapé.');
INSERT INTO RECETTE(NOM_RECETTE, NOTE, CONTENU) VALUES ('Omelette', 2, 'Casser 3 œufs dans un bol, mélanger, et y rajouter sel, poivre, et 20 ml de crème fraîche. Couper des oignons et des dés de tomates. Faire frire les oignons et dés de tomates dans une poêle jusqu''à les dorer, et ensuite rajouter les oeufs.');
INSERT INTO RECETTE(NOM_RECETTE, NOTE, CONTENU) VALUES ('Poulet Crème Fraîche', 4, 'Couper, en petits morceaux, du blanc de poulet. Ajouter du sel et du poivre. Dans une poële, faire frire le poulet. Rajouter ensuite de la crème fraiche, ainsi que des champignons. Laisser cuire jusqu''à... je sais pas moi');
INSERT INTO RECETTE(NOM_RECETTE, NOTE, CONTENU) VALUES ('Salade Poulet Fromage', 5, 'Ajouter de la salade dans un bol, des morceaux de poulets fris, de l''emmental, de la sauce tartare');
INSERT INTO RECETTE(NOM_RECETTE, NOTE, CONTENU) VALUES ('Smoothie Banane Flocon d''Avoine', 3, '300ml de lait, 200g de fromage blanc, 1 banane, 50g de flocon d''avoine, miel, et mettre tout ça dans un mixeur');
INSERT INTO RECETTE(NOM_RECETTE, NOTE, CONTENU) VALUES ('Sandwich au thon', 1, 'Prendre du pain, et mettre du thon dedans. Not exactly rocket science.');